document.addEventListener('DOMContentLoaded', function() {
  (function($) {
    $(function() {

      $('.tab__caption').on('click', 'li:not(.active)', function() {
        $(this)
          .addClass('active').siblings().removeClass('active')
          .closest('.tab').find('.tab__content').removeClass('active').eq($(this).index()).addClass('active');
      });

    });
  })(jQuery);
  (function($) {
    $(function() {

      $('.tab__caption').on('click', 'li:not(.active)', function() {
        $(this)
          .addClass('active').siblings().removeClass('active')
          .closest('.tab').find('.tab__content').removeClass('active').eq($(this).index()).addClass('active');
      });

    });
  })(jQuery);
});
$(window).on('load', function() {
  (function($) {
    $(".works__list").mixItUp({
      selectors: {
        target: '.mix',
        filter: '.filter',
      },
      animation: {
        enable: true,
        duration: 700
      }
    });

    $(".works__item").hover(
      function() {
        $('.works__item').removeClass('active')
        $(this).addClass('animated pulse active');
      },
      function() {
        $(this).removeClass('animated pulse active');
      }
    );
  })(jQuery);
});

$(document).ready(function() {
  (function($) {
    $('#form').bootstrapValidator({
        // To use feedback icons, ensure that you use Bootstrap v3.1.0 or later
        feedbackIcons: {
          valid: 'glyphicon glyphicon-ok',
          invalid: 'glyphicon glyphicon-remove',
          validating: 'glyphicon glyphicon-refresh'
        },
        fields: {
          first_name: {
            validators: {
              stringLength: {
                min: 2,
              },
              notEmpty: {
                message: 'Please supply your first name'
              }
            }
          },
          last_name: {
            validators: {
              stringLength: {
                min: 2,
              },
              notEmpty: {
                message: 'Please supply your last name'
              }
            }
          },
          email: {
            validators: {
              notEmpty: {
                message: 'Please supply your email address'
              },
              emailAddress: {
                message: 'Please supply a valid email address'
              }
            }
          },
          phone: {
            validators: {
              notEmpty: {
                message: 'Please supply your phone number'
              },
              phone: {
                country: 'US',
                message: 'Please supply a vaild phone number with area code'
              }
            }
          },
          address: {
            validators: {
              stringLength: {
                min: 8,
              },
              notEmpty: {
                message: 'Please supply your street address'
              }
            }
          },
          city: {
            validators: {
              stringLength: {
                min: 4,
              },
              notEmpty: {
                message: 'Please supply your city'
              }
            }
          },
          state: {
            validators: {
              notEmpty: {
                message: 'Please select your state'
              }
            }
          },
          zip: {
            validators: {
              notEmpty: {
                message: 'Please supply your zip code'
              },
              zipCode: {
                country: 'US',
                message: 'Please supply a vaild zip code'
              }
            }
          },
          comment: {
            validators: {
              stringLength: {
                min: 10,
                max: 200,
                message: 'Please enter at least 10 characters and no more than 200'
              },
              notEmpty: {
                message: 'Please supply a description of your project'
              }
            }
          }
        }
      })
      .on("submit", function(event) {
        event.preventDefault();
        submitForm();
      });

    function submitForm() {
      $.ajax({
        url: 'php/mail.php', //url страницы (action_ajax_form.php)
        type: "POST", //метод отправки
        dataType: "html", //формат данных
        data: $('#form').serialize(), // Сеарилизуем объект
        success: function(response) { //Данные отправлены успешно
          console.log(1);
        },
        error: function(response) {}
      });
    }
  })(jQuery);
});
$(window).on('load', function() {
  if (/Android|webOS|iPhone|iPad|iPod|BlackBerry/i.test(navigator.userAgent)) {
    $('body').addClass('ios');
  } else {
    $('body').addClass('web');
  };
  $('body').removeClass('loaded');
});