
$(window).on('load', function(){
	$(".container").mixItUp({
	selectors: {
			target:'.mix',
			filter:'.filter',
		},
		animation: {
			enable:true,
			duration:700
		 }
	});

	$('.controls .catalog__btn').on('click', function(){
		$('.controls .catalog__btn').removeClass('active');
		$(this).addClass('active');
	});
});

$(window).on('load', function(){
  $(".js-slider").slick({
    lazyLoad: 'ondemand',
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    centerPadding: '50px',
    prevArrow:'<button class="prevArrow slider__arrow slider__prev"><span></span></button>',
    nextArrow:'<button class="nextArrow slider__arrow slider__next"><span></span></button>'
  });
});

$(window).on('load', function(){
	if( /Android|webOS|iPhone|iPad|iPod|BlackBerry/i.test(navigator.userAgent) ) {
		$('body').addClass('ios');
	} else{
		$('body').addClass('web');
	};
	$('body').removeClass('loaded');
});

